package Leetcode.week.week396;

import org.junit.Test;

public class HOT100283 {
    /*
    同位字符串连接的最小长度
     */

    public int minAnagramLength(String s) {
        int[] word = new int[26];
        int max = Integer.MAX_VALUE;
        for (int i = 0;i < s.length();i++){
            int index = s.charAt(i) - 'a';
            word[index]++;
        }

        for (int n : word) {
            if (n != 0) {
                max = max == Integer.MAX_VALUE ? n : gcd(max, n);
            }
        }
        int res = 0;

        for (int j : word) {
            res += j / max;
        }

        return res;
    }

    private int gcd(int x, int y) {
        while (y != 0) {
            int t = y;
            y = x % y;
            x = t;
        }
        return x;
    }

    @Test
    public void test(){
        System.out.println(minAnagramLength(""));
    }
}
